#include<bits/stdc++.h>
using namespace std;
int main()
{
    int l1;
    int a[100];
    int n,k;
    while(cin>>n>>k)
    {
        l1=0;
        for(int i=0; i<n; i++)
        {
            cin>>a[i];
        }
        int scr=a[k-1];
        for(int i=0; i<n; i++)
        {
            if(a[i]>=scr&&a[i]!=0)
            {
 
                l1=l1+1;
            }
        }
        cout<<l1<<endl;
    }
    return 0;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               